import React, { Component } from 'react';

// import { Container } from './styles';

class TodoList  extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      items: [],
      isModalOpen: false,
      editItem: false,
      isModalEditOpen: false,
    }
  }

  handleInput = (event) => {
    this.setState({ value: event.target.value});
  }

  handleAdd = () => {
    this.setState({ 
      value: '',
      items: [...this.state.items, this.state.value],
      editItem: false,
    })
  }

  handleDeleteAll = () => {
    this.setState({
      items: []
    })
  }

  /* aqui o itenzinho é o item, mas com outro nome para não dar conflito */
  handleDelete = (itenzinho) => {
    this.setState({
      items: this.state.items.filter(item => item !== itenzinho)
    })
  }

  handleEditItem = (itenzinho) => {
    this.setState({
      items: this.state.items.filter(item => item !== itenzinho),
      isModalEditOpen: true,
    })

    this.renderEditItem()
  }

  renderEditItem = (itenzinho) => {
    if (this.state.isModalEditOpen && this.state.items.filter(item => item !== itenzinho)) {
      return (
        <div style={{ position: 'absolute', backgroundColor: '#c7c7c7' }}>
          <div>
            <input value={this.state.value} type="text" onChange={() => this.handleInput(this.state.value)} />
          </div>
        </div>
      )
    }
  }

  handleOpenModal = () => {
    this.setState({ isModalOpen: true });
  }

  handleCloseModal = () => {
    this.setState({ isModalOpen: false })
  }

  renderDelConfirmation = () => {
    return (
      <div style={{ position: 'absolute', backgroundColor: '#c7c7c7' }}>
        <div style={{ padding: '1rem', display: 'flex', justifyContent: 'center', flexDirection: 'column', alignItems: 'center' }}>
          <p>Excluindo essa task você deixa ter tê-la em sua lista</p>
          <span>
            <button onClick={this.handleCloseModal}>fechar</button>
          </span>
        </div>
      </div>
    );
  }

  render() {
    return (
      <div style={{ position: 'relative', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
				<h1>TODO LIST</h1>
        <span style={{ display: 'flex'}}>
          <input value={this.state.value} type="text" onChange={this.handleInput} />
          <button onClick={this.handleAdd}>add</button>
          <button onClick={this.handleDeleteAll}>delete all</button>
        </span>
        <div>
          <ul>
            {
              this.state.items.map((item, index) => {
                return (
                  <div key={index} style={{ display: 'flex'}}>
                    <li key={index}>{item}</li>
                    <button onClick={this.handleOpenModal}>CLICK</button>
                    <button onClick={() => this.handleDelete(item)}>delete</button>
                    <button onClick={() => this.handleEditItem(item)}>edit</button>
                  </div>
                );
              })
            }
          </ul>
        </div>
        {this.state.isModalOpen && this.renderDelConfirmation()}
        {this.state.isModalEditOpen && this.renderEditItem()}
			</div>
    );
  }
}

export default TodoList;
