import React, { Component } from 'react';

// import { Container } from './styles';

class TodoList  extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      items: [],
    }
  }

  handleInput = (event) => {
    this.setState({ value: event.target.value});
  }

  handleAdd = () => {
    this.setState({ 
      value: '',
      items: [...this.state.items, this.state.value]
    })
  }

/*   handleDeleteAll = () => {
    this.setState({
      items: this.state.items.filter(item => item !== this.state.items)
    })
  }
 */
  handleDelete = (itenzinho) => {
    this.setState({
      items: this.state.items.filter(item => item !== itenzinho)
    })
  }

  renderDelConfirmation = () => {
    return (
      <div>
        <p>a</p>
      </div>
    );
  }

  render() {
    return (
      <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
				<h1>TODO LIST</h1>
        <span style={{ display: 'flex'}}>
          <input value={this.state.value} type="text" onChange={this.handleInput} />
          <button onClick={this.handleAdd}>add</button>
         {/*  <button onClick={() => this.handleDeleteAll()}>delete all</button> */}
        </span>
        <div>
          <ul>
            {
              this.state.items.map((item, index) => {
                return (
                  <div key={index} style={{ display: 'flex'}}>
                    <li key={index}>{item}</li>
                    <button onClick={() => this.handleDelete(item)}>delete</button>
                  </div>
                );
              })
            }
          </ul>
        </div>
			</div>
    );
  }
}

export default TodoList;
