import React, { Component } from 'react';

// import { Container } from './styles';

class TodoList  extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      items: [],
      isModalOpen: false,
    }
  }

  handleInput = (event) => {
    this.setState({ value: event.target.value});
  }

  handleAdd = () => {
    this.setState({ 
      value: '',
      items: [...this.state.items, this.state.value]
    })
  }

/*   handleDeleteAll = () => {
    this.setState({
      items: this.state.items.filter(item => item !== this.state.items)
    })
  }
 */
  handleDelete = (itenzinho) => {
    this.setState({
      items: this.state.items.filter(item => item !== itenzinho)
    })
  }

  handleOpenModal = () => {
    this.setState({ isModalOpen: true });
  }

  handleCloseModal = () => {
    this.setState({ isModalOpen: false })
  }

  renderDelConfirmation = (item) => {
    console.log(item);
    return (
      <div style={{ position: 'absolute', zIndex: 3, display: 'flex', backgroundColor: '#9292924f' }}>
        <div>
          <p>VocÃª quer realmente excluir essa task?</p>
          <span>
            <button onClick={this.handleCloseModal}>fechar</button>
            <button onClick={() => this.handleDelete(item)}>delete</button>
          </span>
        </div>
      </div>
    );
  }

  render() {
    return (
      <div style={{ position: 'relative', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center'}}>
				<h1>TODO LIST</h1>
        <span style={{ display: 'flex'}}>
          <input value={this.state.value} type="text" onChange={this.handleInput} />
          <button onClick={this.handleAdd}>add</button>
         {/*  <button onClick={() => this.handleDeleteAll()}>delete all</button> */}
        </span>
        <div>
          <ul>
            {
              this.state.items.map((item, index) => {
                return (
                  <div key={index} style={{ display: 'flex'}}>
                    <li key={index}>{item}</li>
                    {/* <button onClick={() => this.handleDelete(item)}>delete</button> */}
                    <button onClick={this.handleOpenModal}>delete</button>
                  </div>
                );
              })
            }
          </ul>
        </div>
        {this.state.isModalOpen && this.renderDelConfirmation()}
			</div>
    );
  }
}

export default TodoList;
